document.addEventListener("DOMContentLoaded",()=>{const grid=new Muuri(".links-grid",{items:".grid-item",layoutDuration:400,layoutEasing:"ease",dragEnabled:true});const filterButtons=document.querySelectorAll(".filter-bar button");filterButtons.forEach(button=>{button.addEventListener("click",()=>{const filterValue=button.getAttribute("data-filter");grid.filter(item=>{const itemCategory=item.getElement().getAttribute("data-category");return filterValue==="all"||itemCategory===filterValue})})})});